import { useState } from 'react';
import AppLayout from '../components/AppLayout';
import Head from 'next/head';
import { Form, Input, Checkbox, Button } from 'antd';

const Signup = () => {
  const [id, setid] = useState('');
  const [nick, setNick] = useState('');
  const [password, setPassword] = useState('');
  const [passwordCheck, setPasswordCheck] = useState('');

  const onSubmit = () => {};
  const onChangeId = () => {};
  const onChangeNick = () => {};
  const onChangePassword = () => {};
  const onChangePasswordChk = () => {};
  const onChangeTerm = () => {};
  return (
    <>
      <Head>
        <link
          rel="stylesheet"
          href="https://cdnjs.cloudflare.com/ajax/libs/antd/3.22.0/antd.css"
        />
      </Head>
      <AppLayout>
        <Form onSubmit={onSubmit} style={{ padding: 10 }}>
          <div>
            <label htmlFor="user-id">아이디</label>
            <br />
            <Input name="user-id" required onChange={onChangeId} />
          </div>
          <div>
            <label htmlFor="user-nick">닉네임</label>
            <br />
            <Input name="user-nick" required onChange={onChangeNick} />
          </div>
          <div>
            <label htmlFor="user-pass">비밀번호</label>
            <br />
            <Input
              name="user-pass"
              type="password"
              required
              onChange={onChangePassword}
            />
          </div>
          <div>
            <label htmlFor="user-pass-chk">비밀번호 체크</label>
            <br />
            <Input
              name="user-pass-chk"
              type="password"
              required
              onChange={onChangePasswordChk}
            />
          </div>
          <div>
            <Checkbox name="user-term" onChange={onChangeTerm}>
              사용약관을 잘 따라주세요.
            </Checkbox>
          </div>
          <div>
            <Button type="primary" htmlType="submit">
              가입하기
            </Button>
          </div>
        </Form>
      </AppLayout>
    </>
  );
};

export default Signup;
